    def __init__(self):
        self._p1 = [145, 221, 201, 167, 189, 189, 159]
        self._p2 = lambda x: ((x|0x7F)^0xAD)//3

    def compose(self):
        return ''.join(chr(self._p2(c)) for c in self._p1)+'/%d.%d'%(2**4+2,4**2)

class _NoiseEngine:
